attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 os;uniform mat4 ol;uniform bool pp;uniform float lo;uniform sampler2D po;uniform sampler2D mp;uniform vec2 ss;uniform float si;uniform vec2 ep;uniform float ii;uniform vec2 is;uniform bool eo;uniform sampler2D ls;varying vec3 gc;varying vec3 gd;varying vec2 cg;varying vec4 dd; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/ss.x);float f=floor($auxpos1.x/ss.y)/ss.y;float g=(texture2D(po,vec2(e,f))).r;float h=(texture2D(mp,vec2(e,f))).r;float i=mod(ii,si);float j=getExpoEaseOutValue(i,g,h,si);if(eo) j=h;float k=getLinearValue(is,j);if(pp){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(ep,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(ep,k);}if($auxpos1.y==a) cg=vec2(0.0,0.0);else if($auxpos1.y==d) cg=vec2(0.0,1.0);else if($auxpos1.y==b) cg=vec2(1.0,0.0);else if($auxpos1.y==c) cg=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=os*ol*vec4(o,1.0);gc=normalize(p);gd=o;dd=texture2D(ls,vec2(k,0.5));}else{float t=getScope(ep,k);mat4 r=getTransMat($position);float u=($auxpos1.y-lo/2.0)*(20.0/lo);float v=($auxpos1.z-lo/2.0)*(20.0/lo);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(ep,k);vec3 o=(r*vec4(t*($auxpos1.y/lo-0.5),t*($auxpos1.z/lo-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=os*ol*vec4(o,1.0);gc=p;gd=o;cg=$auxpos1.yz/(lo-1.0);}}
